import React, { useState, useEffect } from 'react';
import { PageFlip } from 'react-page-flip';
import './PassportBook.css';

const PassportBook = () => {
  const [currentPage, setCurrentPage] = useState(0);
  const [isFlipping, setIsFlipping] = useState(false);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
    setIsFlipping(true);
  };

  useEffect(() => {
    if (isFlipping) {
      setTimeout(() => {
        setIsFlipping(false);
      }, 1000);
    }
  }, [isFlipping]);

  return (
    <div className="passport-book">
      <PageFlip
        width={400}
        height={600}
        onPageChange={handlePageChange}
        currentPage={currentPage}
        isFlipping={isFlipping}
      >
        {/* Page 1 */}
        <div className="page">
          <h1>Page 1</h1>
          <p>This is the first page of the passport book.</p>
        </div>

        {/* Page 2 */}
        <div className="page">
          <h1>Page 2</h1>
          <p>This is the second page of the passport book.</p>
        </div>

        {/* Add more pages as needed */}
      </PageFlip>
    </div>
  );
};

export default PassportBook;
