import React, { useState, useEffect, useRef } from 'react';
import '../components/book.css'

// eslint-disable-next-line react-hooks/rules-of-hooks
const [page, setPage] = useState(0);
// eslint-disable-next-line react-hooks/rules-of-hooks
const [totalPage, setTotalPage] = useState(0);
// eslint-disable-next-line react-hooks/rules-of-hooks
const [orientation, setOrientation] = useState("landscape");
// eslint-disable-next-line react-hooks/rules-of-hooks
const [state, setState] = useState("read");
// eslint-disable-next-line react-hooks/rules-of-hooks
const flipBookRef = useRef(null);



const FlipBook = ({ pages, width, height }) => {
  const [current, setCurrent] = useState(0);
  const bookRef = useRef(null);



  useEffect(() => {
    const book = bookRef.current;
    const pages = book.querySelectorAll('.page');

    const flip = (e) => {
      const page = e.target.closest('.page');
      const index = [...pages].indexOf(page);

      // Flip the page
      page.classList.add('flipping');
      setTimeout(() => {
        page.classList.remove('flipping');
      }, 1000);

      // Update the current page
      setCurrent(index + (e.target === page ? 1 : -1));
    };

    // Add event listeners for flipping
    pages.forEach(page => {
      page.addEventListener('click', flip);
    });

    return () => {
      pages.forEach(page => {
        page.removeEventListener('click', flip);
      });
    };
  }, []);

  return (
    <div className="flip-book" ref={bookRef} style={{ width, height }}>
      {pages.map((page, index) => (
        <div key={index} className="page" style={{ 
          width, 
          height, 
          backgroundImage: `url(${page})` 
        }}>
          {index === current && (
            <div className="overlay">
              {/* Add content here */}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default FlipBook;
