import React, { useState } from 'react';
import './Passport.css';

const Passport = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleOpen = () => {
    setIsOpen(true);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  return (
    <div className="passport">
      <div className="cover" onClick={handleOpen}>
        <h2>Passport</h2>
        <p>John Doe</p>
        <p> Passport Number: XXXXXXXX</p>
      </div>
      {isOpen && (
        <div className="pages">
     
        </div>
      )}
      <button className="close-btn" onClick={handleClose}>
        Close
      </button>
    </div>
  );
};

export default Passport;
